<?php function navBarForAll($page,$visible=false,$isFixe=true){ 
    if ($visible==true){
        $disp="flex";
    }else{
        $disp="none";
    }
    if ($isFixe==true){
        $pos="fixed";
    }else{
        $pos="relative";
    }
    echo 
        "<style>
        .navBarForAll{
            display: ".$disp.";
            width:100vw;
            height:60px;
            justify-content: center;
            align-items: center;
            background-color: #000913;
            position: ".$pos.";
            top: 0;
            z-index: 60;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
        }
        </style>;
        <script>
        document.addEventListener('DOMContentLoaded', function() {
            const logo1 = document.querySelector('.logo');
            
            logo1.addEventListener('click', function() {
                location.reload(); 
            });
        });    
    </script>"
        ?>
    <div class="navBarForAll"> <?php
        callNavContent(false,$page);
        ?>
    </div><?php
   
} ?>